import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Register from './Register';
import Login from './Login';
import Welcome from './Welcome';
import { jwtDecode } from 'jwt-decode';

const App = () => {
  const [loggedInUser, setLoggedInUser] = useState(() => {
    const token = localStorage.getItem('authToken');
    if (token) {
      try {
        const decodedToken = jwtDecode(token);
        return decodedToken.username;
      } catch (error) {
        console.error('Invalid token:', error);
        return null;
      }
    }
    return null;
  });

  const handleLogin = (username, token) => {
    setLoggedInUser(username);
    localStorage.setItem('authToken', token); // Store token in localStorage
  };

  const handleLogout = () => {
    localStorage.removeItem('authToken'); // Remove token from localStorage on logout
    setLoggedInUser(null);
  };

  const handleRegister = (username) => {
    setLoggedInUser(username);
  };

  useEffect(() => {
    const token = localStorage.getItem('authToken');
    if (token) {
      try {
        const decodedToken = jwtDecode(token);
        console.log('Decoded Token:', decodedToken);
        setLoggedInUser(decodedToken.username); // Update loggedInUser with the decoded username
      } catch (error) {
        console.error('Invalid token:', error);
      }
    }
  }, []);

  return (
    <Router>
      <Routes>
        <Route path="/register" element={<Register onRegister={handleRegister} />} />
        <Route path="/login" element={<Login onLogin={handleLogin} />} />
        <Route
          path="/welcome"
          element={loggedInUser ? <Welcome username={loggedInUser} onLogout={handleLogout} /> : <Navigate to="/login" />}
        />
        <Route path="/" element={<Navigate to={loggedInUser ? '/welcome' : '/login'} />} />
      </Routes>
    </Router>
  );
};

export default App;
