import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Welcome.css';

const Welcome = ({ username, onLogout }) => {
  const [dropdownVisible, setDropdownVisible] = useState(false);
  const [fileUploadVisible, setFileUploadVisible] = useState(false);
  const [cadFiles, setCadFiles] = useState(null);
  const [windFiles, setWindFiles] = useState(null);
  const navigate = useNavigate();

  const handleDropdownToggle = () => {
    setDropdownVisible(!dropdownVisible);
  };

  const handleChangePassword = () => {
    navigate('/changepassword');
  };

  const handleDeleteUser = () => {
    navigate('/deleteUser');
  };

  const handleGadenClick = () => {
    setFileUploadVisible(true);
  };

  const handleFileChange = (e) => {
    const { name, files } = e.target;
    if (name === 'cadFiles') {
      setCadFiles(files);
    } else if (name === 'windFiles') {
      setWindFiles(files);
    }
  };

  const handleFileSubmit = () => {
    // Handle the file submission logic here
    // For now, just log the files to the console
    console.log('CAD Files:', cadFiles);
    console.log('Wind Files:', windFiles);

    // After handling, you can hide the file upload inputs
    setFileUploadVisible(false);
  };

  return (
    <div className="welcome-container">
      <div className="welcome-banner">
        <span className="username" onClick={handleDropdownToggle}>Hi {username}!</span>
        {dropdownVisible && (
          <div className="dropdown-menu">
            <button onClick={handleChangePassword}>Change Password</button>
            <button onClick={handleDeleteUser}>Delete User</button>
          </div>
        )}
        <button onClick={onLogout}>Logout</button>
      </div>
      <div className="main-content">
        <button className="gaden-button" onClick={handleGadenClick}>Gaden</button>
        {fileUploadVisible && (
          <div className="file-upload-container">
            <div>
              <label htmlFor="cadFiles">Upload CAD Files:</label>
              <input type="file" name="cadFiles" multiple onChange={handleFileChange} />
            </div>
            <div>
              <label htmlFor="windFiles">Upload Wind Files:</label>
              <input type="file" name="windFiles" multiple onChange={handleFileChange} />
            </div>
            <button onClick={handleFileSubmit}>Submit</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Welcome;
