import React, { useState}  from 'react';
import { useNavigate } from 'react-router-dom';
import './Welcome.css';

const Welcome = ({ username, onLogout }) => {
  const [dropdownVisible, setDropdownVisible] = useState(false);
  const [buttonVisible, setButtonVisible] = useState(true); // State to manage button visibility
  const navigate = useNavigate();

  const handleDropdownToggle = () => {
    setDropdownVisible(!dropdownVisible);
  };

  const handleChangePassword = () => {
    navigate('/changepassword');
  };

  const handleDeleteUser = () => {
    navigate('/deleteUser');
  };

  const handleGadenClick = () => {
    // Show file input dialog and hide the Gaden button
    const fileInput = document.getElementById('fileInput');
    if (fileInput) {
      fileInput.click(); // Simulate a click on the file input element
      setButtonVisible(false); // Hide the Gaden button
    }
  };

  const handleFileChange = (event) => {
    const files = event.target.files;
    // Handle the selected files here (e.g., send them to backend, process, etc.)
    console.log('Selected files:', files);
  };

  return (
    <div className="welcome-container">
      <div className="welcome-banner">
        <span className="username" onClick={handleDropdownToggle}>Hi {username}!</span>
        {dropdownVisible && (
          <div className="dropdown-menu">
            <button onClick={handleChangePassword}>Change Password</button>
            <button onClick={handleDeleteUser}>Delete User</button>
          </div>
        )}
        <button onClick={onLogout}>Logout</button>
      </div>
      <div className="main-content">
        {buttonVisible && (
          <button className="gaden-button" onClick={handleGadenClick}>Gaden</button>
        )}
        <input type="file" id="fileInput" style={{ display: 'none' }} onChange={handleFileChange} />
      </div>
    </div>
  );
};

export default Welcome;
