import React, { useState,useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Welcome.css';

const Welcome = ({ username, onLogout }) => {
  const [dropdownVisible, setDropdownVisible] = useState(false);
  const [fileInputVisible, setFileInputVisible] = useState(false);
  const [files, setFiles] = useState({ cadFiles: null, windFiles: null });
  const [simulationNumber, setSimulationNumber] = useState(1); // Initialize simulation number
  const navigate = useNavigate();

  useEffect(() => {
    // Load simulation number from local storage on component mount
    const savedSimulationNumber = localStorage.getItem('simulationNumber');
    if (savedSimulationNumber) {
      setSimulationNumber(parseInt(savedSimulationNumber));
    }
  }, []);

  const saveSimulationNumber = (number) => {
    localStorage.setItem('simulationNumber', number.toString());
  };

  const handleDropdownToggle = () => {
    setDropdownVisible(!dropdownVisible);
  };

  const handleChangePassword = () => {
    navigate('/changepassword');
  };

  const handleDeleteUser = () => {
    navigate('/deleteUser');
  };

  const handleGadenClick = () => {
    setFileInputVisible(true);
  };

  const handleFileChange = (e) => {
    const { name, files } = e.target;
    setFiles((prevFiles) => ({
      ...prevFiles,
      [name]: files,
    }));
  };

  const handleFileSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('username', username);
    formData.append('simulationNumber', `${username}_${simulationNumber}`); // Use dynamic simulation identifier

    if (files.cadFiles) {
      Array.from(files.cadFiles).forEach((file) =>
        formData.append('cadFiles', file)
      );
    }
    if (files.windFiles) {
      Array.from(files.windFiles).forEach((file) =>
        formData.append('windFiles', file)
      );
    }

    try {
      const response = await fetch('http://localhost:3000/uploadFiles', {
        method: 'POST',
        body: formData,
      });

      if (response.ok) {
        alert('Files uploaded successfully.');

        // Increment simulation number after successful upload
        setSimulationNumber(simulationNumber + 1);
        saveSimulationNumber(simulationNumber + 1); // Save to local storage
      } else {
        alert('Failed to upload files.');
      }
    } catch (error) {
      console.error('Error:', error);
      alert('Error uploading files.');
    }

    setFileInputVisible(false);
  };

  return (
    <div className="welcome-container">
      <div className="welcome-banner">
        <span className="username" onClick={handleDropdownToggle}>
          Hi {username}!
        </span>
        {dropdownVisible && (
          <div className="dropdown-menu">
            <button onClick={handleChangePassword}>Change Password</button>
            <button onClick={handleDeleteUser}>Delete User</button>
          </div>
        )}
        <button onClick={onLogout}>Logout</button>
      </div>
      <div className="main-content">
        {!fileInputVisible ? (
          <button className="gaden-button" onClick={handleGadenClick}>
            Gaden
          </button>
        ) : (
          <form onSubmit={handleFileSubmit} className="file-upload-form">
            <div>
              <label htmlFor="cadFiles">CAD Files:</label>
              <input
                type="file"
                id="cadFiles"
                name="cadFiles"
                multiple
                onChange={handleFileChange}
              />
            </div>
            <div>
              <label htmlFor="windFiles">Wind Files:</label>
              <input
                type="file"
                id="windFiles"
                name="windFiles"
                multiple
                onChange={handleFileChange}
              />
            </div>
            <button type="submit">Submit</button>
          </form>
        )}
      </div>
    </div>
  );
};

export default Welcome;
