import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Register from './Register';
import Login from './Login';
import Welcome from './Welcome';

const App = () => {
  const [loggedInUser, setLoggedInUser] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('authToken');
    if (token) {
      const { username } = JSON.parse(atob(token.split('.')[1]));
      setLoggedInUser(username);
    }
  }, []);

  const handleLogin = (username) => {
    setLoggedInUser(username);
  };

  const handleLogout = () => {
    localStorage.removeItem('authToken'); // Remove token from localStorage on logout
    setLoggedInUser(null);
  };

  const handleRegister = (username) => {
    setLoggedInUser(username);
  };

  return (
    <Router>
      <Routes>
        <Route path="/register" element={<Register onRegister={handleRegister} />} />
        <Route path="/login" element={<Login onLogin={handleLogin} />} />
        <Route
          path="/welcome"
          element={loggedInUser ? <Welcome username={loggedInUser} onLogout={handleLogout} /> : <Navigate to="/login" />}
        />
        <Route path="/" element={<Navigate to={loggedInUser ? '/welcome' : '/login'} />} />
      </Routes>
    </Router>
  );
};

export default App;
