import React, { useState}  from 'react';
import { useNavigate } from 'react-router-dom';
import './Welcome.css';

const Welcome = ({ username, onLogout }) => {
  const [dropdownVisible, setDropdownVisible] = useState(false);
  const [fileFormVisible, setFileFormVisible] = useState(false); // State to manage form visibility
  const [files, setFiles] = useState(null); // State to manage selected files
  const navigate = useNavigate();

  const handleDropdownToggle = () => {
    setDropdownVisible(!dropdownVisible);
  };

  const handleChangePassword = () => {
    navigate('/changepassword');
  };

  const handleDeleteUser = () => {
    navigate('/deleteUser');
  };

  const handleGadenClick = () => {
    setFileFormVisible(true); // Show the file input form
  };

  const handleFileInputChange = (event) => {
    const selectedFiles = event.target.files;
    setFiles(selectedFiles); // Store the selected files in state
  };

  const handleFileFormSubmit = (event) => {
    event.preventDefault();
    // Handle form submission with files (e.g., send files to backend)
    console.log('Submitted files:', files);
    // Reset form visibility and selected files
    setFileFormVisible(false);
    setFiles(null);
  };

  return (
    <div className="welcome-container">
      <div className="welcome-banner">
        <span className="username" onClick={handleDropdownToggle}>Hi {username}!</span>
        {dropdownVisible && (
          <div className="dropdown-menu">
            <button onClick={handleChangePassword}>Change Password</button>
            <button onClick={handleDeleteUser}>Delete User</button>
          </div>
        )}
        <button onClick={onLogout}>Logout</button>
      </div>
      <div className="main-content">
        {!fileFormVisible && (
          <button className="gaden-button" onClick={handleGadenClick}>Gaden</button>
        )}
        {fileFormVisible && (
          <form className="file-input-form" onSubmit={handleFileFormSubmit}>
            <input type="file" multiple onChange={handleFileInputChange} />
            <button type="submit">Submit</button>
          </form>
        )}
      </div>
    </div>
  );
};

export default Welcome;
