import React, { useState}  from 'react';
import { useNavigate } from 'react-router-dom';
import './Welcome.css';

const Welcome = ({ username, onLogout }) => {
  const [dropdownVisible, setDropdownVisible] = useState(false);
  const [file, setFile] = useState(null);
  const [simulationNumber, setSimulationNumber] = useState(1);
  const [uploading, setUploading] = useState(false);
  const navigate = useNavigate();

  const handleDropdownToggle = () => {
    setDropdownVisible(!dropdownVisible);
  };

  const handleChangePassword = () => {
    navigate('/changepassword');
  };

  const handleDeleteUser = () => {
    navigate('/deleteUser');
  };

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!file) {
      alert('Please select a file to upload');
      return;
    }

    const formData = new FormData();
    formData.append('file', file);
    formData.append('username', username);
    formData.append('simulationNumber', simulationNumber);

    setUploading(true);

    try {
      const response = await fetch('http://localhost:3000/submitSimulation', {
        method: 'POST',
        body: formData,
      });

      if (response.ok) {
        alert('File uploaded successfully');
        setSimulationNumber(simulationNumber + 1); // Increment the simulation number
        setFile(null); // Reset file input
      } else {
        alert('Failed to upload file');
      }
    } catch (error) {
      console.error('Error uploading file:', error);
      alert('An error occurred while uploading the file');
    } finally {
      setUploading(false);
    }
  };

  return (
    <div className="welcome-container">
      <div className="welcome-banner">
        <span className="username" onClick={handleDropdownToggle}>Hi {username}!</span>
        {dropdownVisible && (
          <div className="dropdown-menu">
            <button onClick={handleChangePassword}>Change Password</button>
            <button onClick={handleDeleteUser}>Delete User</button>
          </div>
        )}
        <button onClick={onLogout}>Logout</button>
      </div>
      <div className="main-content">
        {!uploading && (
          <form onSubmit={handleSubmit}>
            <input type="file" onChange={handleFileChange} />
            <button type="submit">Submit</button>
          </form>
        )}
        <button
          className="gaden-button"
          style={{ display: uploading ? 'none' : 'block' }}
          onClick={() => document.querySelector('input[type="file"]').click()}
        >
          Gaden
        </button>
      </div>
    </div>
  );
};

export default Welcome;
