import React, { useState,useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Register from './Register';
import Login from './Login';
import Welcome from './Welcome';
import DeleteUser from './DeleteUser';
import { jwtDecode } from 'jwt-decode';

const App = () => {
  const [loggedInUser, setLoggedInUser] = useState(() => {
    const token = localStorage.getItem('authToken');
    if (token) {
      try {
        const decodedToken = jwtDecode(token);
        if (decodedToken.exp * 1000 < Date.now()) {
          localStorage.removeItem('authToken');
          return null;
        }
        return decodedToken.username;
      } catch (error) {
        console.error('Invalid token:', error);
        localStorage.removeItem('authToken'); // Remove invalid token
        return null;
      }
    }
    return null;
  });

  const handleLogin = (username, token) => {
    setLoggedInUser(username);
    localStorage.setItem('authToken', token); // Store token in localStorage
  };

  const handleLogout = () => {
    localStorage.removeItem('authToken'); // Remove token from localStorage on logout
    setLoggedInUser(null);
  };

  const handleRegister = (username) => {
    setLoggedInUser(username);
  };

  const handleJWT = () => {
    const token = localStorage.getItem('authToken');
    if (token) {
      try {
        const decodedToken = jwtDecode(token);
        if (decodedToken.exp * 1000 < Date.now()) {
          localStorage.removeItem('authToken');
          setLoggedInUser(null);
        } else {
          setLoggedInUser(decodedToken.username); // Update loggedInUser with the decoded username
        }
      } catch (error) {
        console.error('Invalid token:', error);
        localStorage.removeItem('authToken'); // Remove invalid token
        setLoggedInUser(null);
      }
    }
  };

  useEffect(() => {
    handleJWT(); // Initial check when component mounts

    const interval = setInterval(() => {
      handleJWT(); // Check token validity periodically
    }, 60000); // Check every minute

    return () => clearInterval(interval); // Clean up interval on component unmount
  }, []);

  return (
    <Router>
      <Routes>
        <Route path="/register" element={<Register onRegister={handleRegister} />} />
        <Route path="/login" element={<Login onLogin={handleLogin} />} />
        <Route path="/welcome" element={loggedInUser ? <Welcome username={loggedInUser} onLogout={handleLogout} /> : <Navigate to="/login" />} />
        <Route path="/deleteUser" element={<DeleteUser onLogout={handleLogout} />} />
        <Route path="/" element={<Navigate to={loggedInUser ? '/welcome' : '/login'} />} />
      </Routes>
    </Router>
  );
};

export default App;
